import React, { Component } from "react";

class LatestPosition extends Component {
  constructor(props) {
    super(props);
    this.state = {
      top: 0
    };
    this.heightRef = ref => {this.refDom = ref};
  }
  componentDidMount() {
    const {clientHeight} = this.refDom;
    console.log(clientHeight);
    // let top = this.state.top
    this.timer = setInterval(() => {
      if(Math.abs(this.state.top)>Number(clientHeight)){
        this.setState({ top: 0});
      }else{
        this.setState({ top: this.state.top + 2 });
      }

    }, 100);
  }

  render() {
    return (
      <div className="latest-position">
        <div className="more-positions">
          <span>最新职位</span>
          <span>更多</span>
        </div>
        <div className="positions-content" ref={this.heightRef} style={{ top: -this.state.top }}>
          <ul>
            {this.props.position.map((v, i) => {
              return (
                <li key={i}>
                  <a href="www.albb.com">{v.content}</a>
                  <em className="time">{v.time}</em>
                  <em className="city" title="北京,杭州">
                    {v.place}
                  </em>
                </li>
              );
            })}
          </ul>
        </div>
      </div>
    );
  }
}

export default LatestPosition;
